package program;

import java.util.Scanner;

public class ODDNO {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
int num=100;

System.out.println("print the 1 to 100 number");
for(int i=1;i<=num;i++) {
	if(i%2!=0) {
		System.out.println(i+"");
	}
}

	}

}
